INSERT INTO `oc_modification` (`modification_id`, `extension_install_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES ('2', '3', 'PagSeguro Checkout Transparente', '19676', 'Valdeir Psr', '1.4.2', 'http://www.valdeirsantana.com.br', '<?xml version="1.0" encoding="utf-8"?>\n<modification>\n  <name>PagSeguro Checkout Transparente</name>\n  <code>19676</code>\n  <version>1.4.2</version>\n  <author>Valdeir Psr</author>\n  <link>http://www.valdeirsantana.com.br</link>\n  <file path="catalog/controller/checkout/checkout.php">\n    <operation>\n      <search trim="true"><![CDATA[$this->load->language(\'checkout/checkout\');]]></search>\n      <add position="after"><![CDATA[\n        //Verifica se estÃ¡ em modo de teste\n        if (!$this->config->get(\'payment_pagseguro_modo_teste\')) {\n          $this->document->addScript(\'https://stc.pagseguro.uol.com.br/pagseguro/api/v2/checkout/pagseguro.directpayment.js\');\n        } else {\n          $this->document->addScript(\'https://stc.sandbox.pagseguro.uol.com.br/pagseguro/api/v2/checkout/pagseguro.directpayment.js\');\n        }\n      ]]></add>\n    </operation>\n  </file>\n  <file path="catalog/view/theme/*/template/common/footer.tpl">\n    <operation error="skip">\n      <search><![CDATA[<p><?php echo $powered; ?></p>]]></search>\n      <add position="after"><![CDATA[\n        <div style="position:absolute;left:-9999px;top:-9999px;"><h1><a href="//www.valdeirsantana.com.br">Valdeir Santana / OpenCart / Lojas Virtuais / Arastta</a></h1></div>\n      ]]></add>\n    </operation>\n  </file>\n</modification>\n', '1', '2018-08-14 00:26:24');
INSERT INTO `oc_modification` (`modification_id`, `extension_install_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES ('6', '0', 'd_opencart_patch', 'd_opencart_patch', 'Dreamvention', '3.0.9', 'http://dreamvention.com', '<?xml version="1.0" encoding="utf-8"?>\n<modification>\n    <name>d_opencart_patch</name>\n    <code>d_opencart_patch</code>\n    <description>Permission fixes in Opencart 3.x</description>\n    <version>3.0.9</version>\n    <author>Dreamvention</author>\n    <link>http://dreamvention.com</link>\n    <file path="admin/controller/user/user_permission.php">\n        <operation error="skip">\n            <search><![CDATA[$path[] = $file . \'/*\';]]></search>\n            <add position="after"><![CDATA[\n            //d_opencart_patch.xml 1\n            $files[] = $file;\n            ]]></add>\n        </operation>\n        <operation error="skip">\n            <search><![CDATA[$permission = substr($controller, 0, strrpos($controller, \'.\'));]]></search>\n            <add position="replace"><![CDATA[\n            //d_opencart_patch.xml 2\n            $permission = (strrpos($controller, \'.\') !== false) ? substr($controller, 0, strrpos($controller, \'.\')) : $controller;\n            ]]></add>\n        </operation>\n    </file>\n    <file path="admin/controller/extension/installer.php">\n        <operation error="skip">\n            <search><![CDATA[$modification_info = $this->model_setting_modification->getModificationByCode($code);]]></search>\n            <add position="after"><![CDATA[\n            //d_opencart_patch.xml 1\n            if (strpos($code, \'d_\') === 0 && $modification_info) {\n                $this->model_setting_modification->deleteModification($modification_info[\'modification_id\']);\n                $modification_info = false; \n            }\n            ]]></add>\n        </operation>\n    </file>\n    <file path="admin/model/setting/extension.php">\n        <operation error="skip">\n            <search><![CDATA[$query = $this->db->query("SELECT * FROM `" . DB_PREFIX . "extension_path` WHERE `extension_install_id` = \'" . (int)$extension_install_id . "\' ORDER BY `date_added` ASC");]]></search>\n            <add position="replace"><![CDATA[\n        //d_opencart_patch.xml 1\n        $query = $this->db->query("SELECT * FROM `" . DB_PREFIX . "extension_install` WHERE `extension_install_id` = \'" . (int)$extension_install_id . "\'");\n\n        $extension = $query->row;\n\n        if(isset($extension[\'filename\']) && strpos($extension[\'filename\'], \'_compiled_\')){\n\n\n            $parts = explode(\'_compiled_\', $extension[\'filename\']);\n            $extension_name = $parts[0];\n            $query = $this->db->query("SELECT * FROM `" . DB_PREFIX . "extension_path` WHERE `extension_install_id` = \'" . (int)$extension_install_id . "\' AND path LIKE \'%".$this->db->escape($extension_name)."%\' ORDER BY `date_added` ASC");\n        }else{\n            $query = $this->db->query("SELECT * FROM `" . DB_PREFIX . "extension_path` WHERE `extension_install_id` = \'" . (int)$extension_install_id . "\' ORDER BY `date_added` ASC");\n        }\n            ]]></add>\n        </operation>\n    </file>\n    <file path="admin/model/setting/modification.php">\n        <operation error="skip">\n            <search><![CDATA[$this->db->query("DELETE FROM `" . DB_PREFIX . "modification` WHERE `extension_install_id` = \'" . (int)$extension_install_id . "\'");]]></search>\n            <add position="replace"><![CDATA[\n        //d_opencart_patch.xml 1\n        $query = $this->db->query("SELECT * FROM `" . DB_PREFIX . "extension_install` WHERE `extension_install_id` = \'" . (int)$extension_install_id . "\'");\n\n        $extension = $query->row;\n\n        if(isset($extension[\'filename\']) && strpos($extension[\'filename\'], \'_compiled_\') !== false){\n\n\n            $parts = explode(\'_compiled_\', $extension[\'filename\']);\n            $extension_name = $parts[0];\n            $this->db->query("DELETE FROM `" . DB_PREFIX . "modification` WHERE `extension_install_id` = \'" . (int)$extension_install_id . "\' AND `name` LIKE \'%".$this->db->escape($extension_name)."%\'");\n\n        }else{\n            $this->db->query("DELETE FROM `" . DB_PREFIX . "modification` WHERE `extension_install_id` = \'" . (int)$extension_install_id . "\'");\n        }\n            ]]></add>\n        </operation>\n    </file>\n    <file path="admin/controller/marketplace/install.php">\n        <operation error="skip">\n            <search><![CDATA[$this->model_setting_extension->deleteExtensionInstall($extension_install_id);]]></search>\n            <add position="replace"><![CDATA[//d_opencart_patch.xml 1 moved down]]></add>\n        </operation>\n    </file>\n    <file path="admin/controller/marketplace/install.php">\n        <operation error="skip">\n            <search><![CDATA[$this->model_setting_modification->deleteModificationsByExtensionInstallId($extension_install_id);]]></search>\n            <add position="after"><![CDATA[\n                //d_opencart_patch.xml 2\n                $this->model_setting_extension->deleteExtensionInstall($extension_install_id);]]></add>\n        </operation>\n    </file>\n</modification>', '1', '2018-08-14 00:47:37');
INSERT INTO `oc_modification` (`modification_id`, `extension_install_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES ('5', '0', 'd_shopunity', 'd_shopunity', 'Dreamvention', '3.1.0', 'http://dreamvention.com', '<?xml version="1.0" encoding="utf-8"?>\n<modification>\n    <name>d_shopunity</name>\n    <code>d_shopunity</code>\n    <description>This is a modification file. You can use it to </description>\n    <version>3.1.0</version>\n    <author>Dreamvention</author>\n    <link>http://dreamvention.com</link>\n    <file path="admin/controller/common/header.php">\n        <operation error="skip">\n            <search><![CDATA[public function index() {]]></search>\n            <ignoreif><![CDATA[$data[\'d_shopunity\'] = $this->url->link(\'extension/d_shopunity/extension\', \'user_token=\'.$this->request->get[\'user_token\'], \'SSL\');]]></ignoreif>\n            <add position="after"><![CDATA[\n            if(!empty($this->request->get[\'user_token\'])){\n                $data[\'d_shopunity\'] = $this->url->link(\'extension/d_shopunity/extension\', \'user_token=\'.$this->request->get[\'user_token\'], \'SSL\');\n            }\n            ]]></add>\n        </operation>\n    </file>\n    <file path="admin/view/template/common/header.twig">\n        <operation error="skip">\n            <search><![CDATA[<li class="dropdown"><a href="#" class="dropdown-toggle" data-toggle="dropdown"><img src="{{ image }}" alt="{{ firstname }} {{ lastname }}" title="{{ username }}" id="user-profile" class="img-circle" />{{ firstname }} {{ lastname }} <i class="fa fa-caret-down fa-fw"></i></a>]]></search>\n            <ignoreif><![CDATA[<li><a href="{{d_shopunity}}"><i class="fa fa-flask fa-lg"></i></a></li>]]></ignoreif>\n            <add position="before"><![CDATA[\n            <li><a href="{{ d_shopunity }}" title="Shopunity"><i class="fa fa-flask fa-lg"></i></a></li>\n            ]]></add>\n        </operation>\n    </file>\n</modification>', '1', '2018-08-14 00:47:37');
INSERT INTO `oc_modification` (`modification_id`, `extension_install_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES ('7', '0', 'd_twig_manager', 'd_twig_manager', 'Dreamvention', '2.0.0', 'http://dreamvention.com', '<?xml version="1.0" encoding="utf-8"?>\n<modification>\n    <name>d_twig_manager</name>\n    <code>d_twig_manager</code>\n    <description>Adds support for twig templating engine</description>\n    <version>2.0.0</version>\n    <author>Dreamvention</author>\n    <link>http://dreamvention.com</link>\n    <file path="system/engine/loader.php">\n        <operation error="skip" info="">\n            <search><![CDATA[$template = new Template($this->registry->get(\'config\')->get(\'template_engine\'));]]></search>\n            <add position="replace"><![CDATA[\n            //d_twig_manager.xml\n            $template = new Template($this->registry->get(\'config\')->get(\'template_engine\'), $this->registry);\n            ]]></add>\n        </operation>\n    </file>\n    <file path="system/library/template.php">\n        <operation error="skip" info="">\n            <search><![CDATA[public function __construct($adaptor) {]]></search>\n            <add position="replace"><![CDATA[\n            //d_twig_manager.xml\n            public function __construct($adaptor, $registry) {\n            ]]></add>\n        </operation>\n        <operation error="skip" info="">\n            <search><![CDATA[$this->adaptor = new $class();]]></search>\n            <add position="replace"><![CDATA[\n            //d_twig_manager.xml\n            $this->adaptor = new $class($registry);\n            ]]></add>\n        </operation>\n    </file>\n    <file path="system/library/template/twig.php">\n        <operation error="skip" info="">\n            <search><![CDATA[private $data = array();]]></search>\n            <add position="after"><![CDATA[\n            //d_twig_manager.xml\n            private $registry = array();\n            ]]></add>\n        </operation>\n        <operation error="skip" info="">\n            <search><![CDATA[public function __construct() {]]></search>\n            <add position="replace"><![CDATA[\n            //d_twig_manager.xml\n            public function __construct($registry) {\n            ]]></add>\n        </operation>\n        <operation error="skip" info="">\n            <search><![CDATA[\\Twig_Autoloader::register();]]></search>\n            <add position="after"><![CDATA[\n            //d_twig_manager.xml\n            $this->registry = $registry;\n            ]]></add>\n        </operation>\n        <operation error="skip" info="">\n            <search><![CDATA[$this->twig = new \\Twig_Environment($loader, $config);]]></search>\n            <add position="after"><![CDATA[\n            //d_twig_manager.xml\n            if (file_exists(DIR_SYSTEM . \'library/template/Twig/Extension/DTwigManager.php\')) {\n                $this->twig->addExtension(new \\Twig_Extension_DTwigManager($this->registry));\n            }\n            ]]></add>\n        </operation>\n    </file>\n    <file path="catalog/controller/event/theme.php">\n        <operation error="skip" info="">\n            <search><![CDATA[$twig = new \\Twig_Environment($loader, $config);]]></search>\n            <add position="after"><![CDATA[\n            //d_twig_manager.xml\n            if (file_exists(DIR_SYSTEM . \'library/template/Twig/Extension/DTwigManager.php\')) {\n                $twig->addExtension(new Twig_Extension_DTwigManager($this->registry));\n            }\n            ]]></add>\n        </operation>\n    </file>\n</modification>', '1', '2018-08-14 00:47:39');
